import React, { Component } from "react";
import { Layout, Row, Col } from "antd";
import { Link } from "react-router-dom";

const { Header, Content, Footer } = Layout;

class Home extends Component {
  constructor(props) {
    super(props);

    this.state = {
        current: 'mail'
    }

}
handleClick = (e) => {
  console.log('click ', e);
  this.setState({
      current: e.key,
  });
}
  render() {
    return (
      <div>
        <Layout>
          <Header>
           
            <Row>
            <Col xs={22} sm={22} md={22} lg={22} xl={22}>
                                <Menu
                                    onClick={this.handleClick}
                                    selectedKeys={[this.state.current]}
                                    mode="horizontal"
                                >
                                    <Menu.Item >
                                        <Link to={{ pathname: "/home" }}> <img src='https://rsrit.com/wp-content/uploads/2017/12/logo_dark.png' width="200px" height="60px"></img></Link>
                                    </Menu.Item>
                                    <Menu.Item key="mail">
                                        <Link to={{ pathname: "/home" }}><Icon type="mail" />Home</Link>
                                    </Menu.Item>
                                    <Menu.Item key="timesheet">
                                        <Link to={{ pathname: "/createNewRequest" }}><Icon type="clock-circle" />TimeSheet</Link>
                                    </Menu.Item>
                                </Menu>
                            </Col>
                            <Col xs={2} sm={2} md={2} lg={2} xl={2}>
                                <Link to={{ pathname: "/login" }}> <Button size='large'><Icon type="logout" />Logout</Button></Link>
                            </Col>

                        </Row>

          </Header>
          <Content
            style={{
              margin: "24px 16px",
              padding: 24,
              background: "#fff",
              minHeight: 580
            }}
          >
            <Row>
              <Col span={8}>
                {" "}
                <Link to={{ pathname: "/createNewRequest" }}>
                  Create TimeSheet
                </Link>{" "}
              </Col>
            </Row>
            <Row>
              <Col span={8}>
                {" "}
                <Link to={{ pathname: "/TimeSheetCalander" }}>
                  Monthly Timeshhet{" "}
                </Link>{" "}
              </Col>
            </Row>
            <Row>
              <Col span={8}>
                {" "}
                <Link to={{ pathname: "/addEmployee" }}>Add Employee</Link>{" "}
              </Col>
            </Row>
            <Row>
              <Col span={8}>
                {" "}
                <Link to={{ pathname: "/listEmployees" }}>
                  List of SignUp Employees
                </Link>{" "}
              </Col>
            </Row>
          </Content>

          <Footer>Footer</Footer>
        </Layout>
      </div>
    );
  }
}

export default Home;
