import React, { Component } from "react";
import { connect } from "react-redux";
import PropTypes from 'prop-types';
import { Link } from "react-router-dom";
import  Validator from 'validator';
import { Form, Input, Button, Select, Checkbox, Icon } from "antd";
import { loginUser, getUsersList } from "../../redux/actions/auth";
const FormItem = Form.Item;
const Option = Select.Option;

class Login extends Component {
  constructor(props) {
    super(props);
    // defining the state to the component
    this.state = {
      data: {
        email: "testbiz@rsrit.com",
        password: "123456",
        isBusiness: true,
        role: "3"
      },
      loading: false,
      errors: {}
    };
  }

  componentDidMount() {}

  static getDerivedStateFromProps(props, state) {
    if (props.isUserLoggedIn) {            
        if(props.isBusiness){
            props.history.push("/home");
        } else{
            props.history.push("/signup");
        }
    } 
    return null;
  }
  onDropdownChange = (e, i) => {
    let data = Object.assign({}, this.state.data);
    data.role = e;
    return this.setState({ data });
  };

  onChange = e => this.setState({
    data: { ...this.state.data, [e.target.name]: e.target.value}
});

  handleSubmit = () => {
    const errors = this.validate(this.state.data);
        this.setState({errors});        
        if(Object.keys(errors).length===0){
            this.props.dispatch(getUsersList());
            this.props.dispatch(loginUser(this.state.data.email, this.state.data.password));
        }
    };

    validate = data => {
        const errors = {};
        if(!Validator.isEmail(data.email)) errors.email = "Invalid Email";
        if(!data.password) errors.password = "Can't be empty";
        return errors;
    }


  render() {
    const { data, errors } = this.state

    const { getFieldDecorator } = this.props.form;
    return (
      <center>
        <Form onSubmit={this.handleSubmit} className="login-form">
          <div>
            <img
              src="https://rsrit.com/wp-content/uploads/2017/12/logo_dark.png"
              width="200px"
              height="60px"
            />
            <br />
          </div>

          <FormItem>
            {getFieldDecorator("userName", {
              rules: [
                { required: true, message: "Please input your username!" }
              ]
            })(
              <Input
                prefix={
                  <Icon type="user" style={{ color: "rgba(0,0,0,.25)" }} />
                }
                placeholder="Username"
              />
            )}
          </FormItem>
          <FormItem>
            {getFieldDecorator("password", {
              rules: [
                { required: true, message: "Please input your Password!" }
              ]
            })(
              <Input
                prefix={
                  <Icon type="lock" style={{ color: "rgba(0,0,0,.25)" }} />
                }
                type="password"
                placeholder="Password"
              />
            )}
          </FormItem>
          <FormItem label="Role">
            <Select
              id="role"
              name="role"
              value={this.state.data.role}
              onChange={this.onDropdownChange}
            >
              <Option value="1">Admin</Option>
              <Option value="2">HR Manager</Option>
              <Option value="3">Employee</Option>
            </Select>
          </FormItem>
          <FormItem>
            {getFieldDecorator("remember", {
              valuePropName: "checked",
              initialValue: true
            })(<Checkbox>Remember me</Checkbox>)}
            <a className="login-form-forgot" href="">
              Forgot password
            </a>
            <Button
              type="primary"
              htmlType="submit"
              className="login-form-button"
            >
              Log in
            </Button>
            Or <Link to="/signup">Sign Up</Link>
          </FormItem>
        </Form>
      </center>
    );
  }
}

Login.propTypes = {
}

const mapStateToProps = state => {
    return {
        isUserLoggedIn: state.auth.loggedIn,
        userObject: state.auth.user,
        isBusiness: state.auth.isBusiness
    };
};
 
export default connect(mapStateToProps)(Login);
