                        {/* <p><Link to={{pathname: "/signup"}}>SignUp</Link></p> */}
                    </Form>
                </Card>
            </div>
         );
    }
}

Login.propTypes = {
}

const mapStateToProps = state => {
    return {
        result:state.auth.result
    };
};

export default connect(mapStateToProps)(Login);
