import React, { Component } from "react";
import { connect } from "react-redux";
import { Link } from "react-router-dom";
import Validator from "validator";
import InlineError from "../messages/InlineError";
import { Col, Form, Input, Button, Select, Card } from "antd";
import { loginUser, getUsersList } from "../../redux/actions/auth";
const FormItem = Form.Item;
const Option = Select.Option;

class Login extends Component {
  constructor(props) {
    super(props);
    // defining the state to the component
    this.state = {
      data: {
        email: "testbiz@rsrit.com",
        password: "123456",
        isBusiness: true,
        role: "3"
      },
      loading: false,
      errors: {}
    };
  }

  componentDidMount() {}

  static getDerivedStateFromProps(props, state) {
    if (props.isUserLoggedIn) {
      if (props.isBusiness) {
        props.history.push("/home");
      } else {
        props.history.push("/signup");
      }
    }
    return null;
  }
  onDropdownChange = (e, i) => {
    let data = Object.assign({}, this.state.data);
    data.role = e;
    return this.setState({ data });
  };

  onChange = e =>
    this.setState({
      data: { ...this.state.data, [e.target.name]: e.target.value }
    });

  handleSubmit = () => {
    const errors = this.validate(this.state.data);
    this.setState({ errors });
    if (Object.keys(errors).length === 0) {
      this.props.dispatch(getUsersList());
      this.props.dispatch(
        loginUser(this.state.data.email, this.state.data.password)
      );
    }
  };

  validate = data => {
    const errors = {};
    if (!Validator.isEmail(data.email)) errors.email = "Invalid Email";
    if (!data.password) errors.password = "Can't be empty";
    return errors;
  };

  render() {
    const { data, errors } = this.state;
    return (
      <center>
        <div>
          <Card title="Login">
          <Col xs={8} sm={8} md={8} lg={8} xl={8}>
            <Form>
              <Form.Item error={!!errors.email}>
                <Input
                  id="email"
                  type="text"
                  name="email"
                  value={data.email}
                  onChange={this.onChange}
                  placeholder="example@example.com"
                />
                {errors.email && <InlineError text={errors.email} />}
              </Form.Item>
              <Form.Item error={!!errors.password}>
                <Input
                  id="password"
                  type="password"
                  name="password"
                  value={data.password}
                  onChange={this.onChange}
                  placeholder="Make it Secure"
                />
                {errors.password && <InlineError text={errors.password} />}
              </Form.Item>
              <FormItem label="Role">
                <Select
                  id="role"
                  name="role"
                  value={this.state.data.role}
                  onChange={this.onDropdownChange}
                >
                  <Option value="1">Admin</Option>
                  <Option value="2">HR Manager</Option>
                  <Option value="3">Employee</Option>
                </Select>
              </FormItem>
              {/* <Checkbox name="isBusiness" checked={data.isBusiness} onChange={this.onChange}>Business</Checkbox> */}
              <Button type="primary" onClick={this.onSubmit}>
                Submit  
              </Button>
                   <Link to="/signup">Sign Up</Link>
            </Form></Col>
          </Card>
        </div>
      </center>
 );
    }
}

Login.propTypes = {};

const mapStateToProps = state => {
  return {
    isUserLoggedIn: state.auth.loggedIn,
    userObject: state.auth.user,
    isBusiness: state.auth.isBusiness
  };
};

export default connect(mapStateToProps)(Login);
