import React, { Component } from 'react';
import { Collapse, Form, Button, Tabs, Layout, Input, Row, Col, DatePicker, Radio, Card, Select, Upload, message, Icon } from 'antd';
import moment from 'moment';
import { connect } from 'react-redux';
import { bindActionCreators } from 'redux';
import { createWorkingHourTimeSheet } from "../../redux/actions/CreateNewRequest";


class timesheet extends Component {

    constructor(props) {
        super(props);
        this.state = {

            workinghours: {
                "monday": {
                    day: 'monday',
                    workHours: '0',
                    timeOffHour: '0',
                    date: 'Test',
                    billableHours: '0',
                    totalHour: '0',
                    notes: 'Test'

                },
                "tuesday": {
                    day: 'tuesday',
                    workHours: '0',
                    timeOffHour: '0',
                    date: '0',
                    billableHours: '0',
                    totalHour: '0',
                    notes: 'Test'

                },
                "wednesday": {
                    day: 'wednesday',
                    workHours: '0',
                    timeOffHour: '0',
                    date: 'Test',
                    billableHours: '0',
                    totalHour: '0',
                    notes: 'Test'

                },
                "thursday": {
                    day: 'thursday',
                    workHours: '0',
                    timeOffHour: '0',
                    date: 'Test',
                    billableHours: '0',
                    totalHour: '0',
                    notes: 'Test'

                }, "friday": {
                    day: 'friday',
                    workHours: '0',
                    timeOffHour: '0',
                    date: 'Test',
                    billableHours: '0',
                    totalHour: '0',
                    notes: 'Test'

                }, "saturday": {
                    day: 'saturday',
                    workHours: '0',
                    timeOffHour: '0',
                    date: 'Test',
                    billableHours: '0',
                    totalHour: '0',
                    notes: 'Test'

                },
                "sunday": {
                    day: 'sunday',
                    workHours: '0',
                    timeOffHour: '0',
                    date: 'Test',
                    billableHours: '0',
                    totalHour: '0',
                    notes: 'Test'

                },

            }
            ,

            Workingdetails: {
                totalWeekWorkHours: '',
                totalWeekBillableHours: '0',
                totalWeekTimeoffHours: '0'

            }

        };


    }



    handlenum1Change = (evt) => {
        let Workingdetails = Object.assign({}, this.state.Workingdetails);
        let workinghours = Object.assign({}, this.state.workinghours);
        let day = workinghours[evt.target.name]
        day.workHours = Number(evt.target.value);
        Workingdetails.totalWeekWorkHours = Workingdetails.totalWeekWorkHours + day.workHours;
        day.billableHours = Number(evt.target.value);
        return this.setState({ workinghours, Workingdetails });
    }

    handlenum2Change = (evt) => {

        let workinghours = Object.assign({}, this.state.workinghours);
        let day = workinghours[evt.target.name]
        day.timeOffHour = Number(evt.target.value);
        day.totalHour = day.workHours + Number(evt.target.value);;
        return this.setState({ workinghours });


    }


    onSubmit = () => {
        let data = {};
        data.workinghours = this.state.workinghours;
        data.Workingdetails = this.state.Workingdetails;
        // data.reqId = this.state.reqDetails.reqId;
        this.props.dispatch(createWorkingHourTimeSheet(data));
        console.log("Save click");
