import React, { Component } from "react";
import { connect } from "react-redux";
import { Card, Form, Input, Menu, Button, Icon, Col, Layout } from "antd";
import { Link } from "react-router-dom";
import InlineError from "../messages/InlineError";
import { searchEmp } from "../../redux/actions/Get_List";

const { Header } = Layout;

class Search extends Component {
  constructor(props) {
    super(props);

    this.state = {
      data: {
        firstName: ""
      },
      loading: false,
      errors: {}
    };
  }

  componentDidMount() {

    this.props.dispatch(G())
}

  onChange = e =>
    this.setState({
      data: { ...this.state.data, [e.target.name]: e.target.value }
    });

  handleClick = e => {
    console.log("click ", e);
    this.setState({
      current: e.key
    });
  };

  validate = data => {
    const errors = {};
    if (!data.firstName) errors.firstName = "Can't be empty";
    return errors;
  };

  onSearch = e => {
    const errors = this.validate(this.state.data);
    this.setState({ errors });
    if (Object.keys(errors).length === 0) {
      const { firstName } = this.state.data;
      this.props.dispatch(
        searchEmp({
          firstName: firstName
        })
      );

      this.props.history.push("/search");
    }
  };

  render() {
    const { data, errors } = this.state;

    return (
      <div>
        <Header>
          <Col xs={22} sm={22} md={22} lg={22} xl={22}>
            <Menu
              onClick={this.handleClick}
              selectedKeys={[this.state.current]}
              mode="horizontal"
            >
              <Menu.Item>
                <Link to={{ pathname: "/home" }}>
                  {" "}
                  <img
                    src="https://rsrit.com/wp-content/uploads/2017/12/logo_dark.png"
                    width="200px"
                    height="60px"
                  />
                </Link>
              </Menu.Item>
            </Menu>
          </Col>
          <Col xs={2} sm={2} md={2} lg={2} xl={2}>
            <Link to={{ pathname: "/login" }}>
              {" "}
              <Button size="large">
                <Icon type="logout" />
                Logout
              </Button>
            </Link>
          </Col>
        </Header>
        <Card title="Search Employee">
          <Form>
            <Col span={8}>
              <Form.Item error={!!errors.firstName}>
                <Input
                  id="firstName"
                  type="text"
                  name="firstName"
                  value={data.firstName}
                  onChange={this.onChange}
                  placeholder="Enter Name"
                />
                {errors.firstName && <InlineError text={errors.firstName} />}
              </Form.Item>
              <Button type="primary" onClick={this.onSearch}>
                Search
              </Button>
            </Col>
          </Form>
        </Card>
      </div>
    );
  }
}
const mapStateToProps = state => {
  return {
    userObject: state.auth.user
  };
};

export default connect(mapStateToProps)(Search);
